import React, { useEffect, useState } from 'react';
import axios from "axios";
import "./Products.css";
import Header from "../components/Header.js";
import Footer from "../components/Footer.js";
// import { Link } from "react-router-dom";




// function random_bg_color() {
//     var x = Math.floor(Math.random() * 256);
//     var y = Math.floor(Math.random() * 256);
//     var z = Math.floor(Math.random() * 256);
//     var bgColor = "rgb(" + x + "," + y + "," + z + ")";
//     console.log(bgColor);

//     document.querySelector("#productCard").style.background = bgColor;
// }

// random_bg_color();




function Products() {

    const [products, setProducts] = useState([])

    useEffect(() => {

        const fetchAllProducts = async () => {

            try {
                const res = await axios.get("http://localhost:8000/products")


                setProducts(res.data);


            } catch (err) {
                console.log(err)
            }

        }

        fetchAllProducts();

    }, [])


    function showMorefunction() {

        document.getElementsByClassName(`showMoreDiv`).style.display = "block";


    }




    return (
        <div>

            <Header />

            {/* HERO SECTION */}
            <section id="heroSection">

                <div className="heroContainer">

                    <p className="heroText">Explore our world of audio</p>

                </div>

                {/* In-Page navigation bar  */}
                {/* <div>
                    <ul className="productsInnerNav">
                        <li className="InnerNav-item"><a href="#proAudioSection" style="text-decoration: none;">Pro Audio</a></li>
                        <li className="InnerNav-item"><a href="#homeAudioSection" style="text-decoration: none;">Home Audio</a></li>
                    </ul>
                </div> */}

            </section>



            <section id="proAudioSection">

                {/*  PRO AUDIO INTRO */}
                <div className="proIntro">
                    <h1 className='Banner'>PRO AUDIO</h1>

                    <p className="proSub">Industry standards and classics. Find them all under one roof!  Every item in this category comes with a two year warranty already included in the price!</p>

                </div>
            </section>


            {/* "../assets/productImages/Images/ManleyVoxbox/voxbox-top-down-view-copy.jpg" */}
            <div className='products'>
                {products.slice(0, 5).map((product) => (
                    <div className='proProductCard' key={product.ID}>

                        <p className='productTitle'>{product.productTitle}</p>
                        <img className='productIMG' src={product.productURL} alt={product.productTitle} />
                        <p className='productDesc'>{product.productDescription}</p>
                        <p className='productPrice'>${product.productPrice} USD</p>

                        <div className='btnContainer'>
                            <button className='showMoreBTN'>Show more!</button>
                            <button className='add2CartBTN'>Add to Cart</button>
                        </div>

                        <div className='showMoreDiv' onClick={showMorefunction()} style={{ display: "none" }}>


                        </div>

                    </div>


                ))}
            </div>



            <section id="homeAudioSection">

                <div className="homeIntro">
                    <h1 className='Banner'>HOME AUDIO</h1>

                    <p className="homeSub">Small packages that pack a big punch! For musicians big and small too! Every item in this category comes with a two year warranty already included in the price!</p>

                </div>
            </section>




            <div className='products'>
                {products.slice(5, 12).map((product) => (
                    <div className='homeProductCard' key={product.ID}>

                        <p className='productTitle'>{product.productTitle}</p>
                        <img className='productIMG' src={product.productURL} alt={product.productTitle} />
                        <p className='productDesc'>{product.productDescription}</p>
                        <p className='productPrice'>${product.productPrice} USD</p>


                        <button className='showMoreBTN'>Show more!</button>

                    </div>

                ))}
            </div>

            {/* <button><Link to="/add">Add new book</Link></button> */}

            <Footer />
        </div >
    )
}

export default Products
