import React, { useEffect, useState } from 'react';
import axios from "axios";
import Header from '../components/Header';
import Footer from '../components/Footer';
import "./Cart.css";

function Cart() {


    const [cartProducts, setCartProducts] = useState([])

    useEffect(() => {

        const fetchAllCartProducts = async () => {

            try {
                const res = await axios.get("http://localhost:8000/cart")


                setCartProducts(res.data);


            } catch (err) {
                console.log(err)
            }

        }

        fetchAllCartProducts();

    }, [])


    const handleDelete = (cartID) => {

        const deleteFromCart = async () => {

            try {
                console.log(cartID);
                const res = await axios.delete(`http://localhost:8000/CartDel/${cartID}`);
                console.log(res);
                window.location.reload();

            } catch (err) {
                console.log(err);
            }

        }

        deleteFromCart();
    }



    return (
        <div className='cartContainer'>
            <Header />


            <div div className='cart' >
                {
                    cartProducts.map((product) => (
                        <div className='proProductCard' key={product.cartID}>

                            <p className='productTitle'>{product.addedProduct}</p>
                            {/* <img className='productIMG' src={product.productURL} alt={product.addedProduct} /> */}
                            <p className='productPrice' >${product.addedPrice} USD</p>

                            <div className='btnContainer'>
                                <button className='add2CartBTN' onClick={() => handleDelete(product.cartID)}>Delete</button>
                            </div>


                        </div>


                    ))
                }
            </div >






            <Footer />

        </div>
    )
}

export default Cart
