import React from 'react';
import { useNavigate } from "react-router-dom";
import "./Homepage.css";
import Header from "../components/Header.js";
import Footer from "../components/Footer.js";
import Slider from '../components/Slider';

function Homepage() {



    const navigate = useNavigate();

    const filterCategory = (category) => {

        const fetchCategory = async () => {

            try {
                const res = await axios.get(`http://localhost:8000/Type/${category}`);

                setProducts(res.data);

            } catch (err) {
                console.log(err);
            }

        }

        fetchCategory();
    }





    return (
        <div>
            <Header />
            <main>

                {/* HERO */}
                <section id="hero">

                    <h1 className='Market'>MARKET AUDIO</h1>

                    <p class="subName"> QUALITY | COMMUNITY | TECHNOLOGY </p>

                </section>

                <div className='superCont'>
                    <Slider />
                </div>


                {/* CATEGORY BOXES */}

                <section id="prodCatBoxes">

                    <div className="boxes">

                        {/* PRO AUDIO CATEGORY BOX */}
                        <div className="box1" onClick={() => {
                            navigate("/Products");
                            filterCategory(`"proAudio"`);
                        }}>
                            <p className="description">Pro Audio</p>
                        </div>

                        {/* HOME AUDIO CATEGORY BOX  */}
                        <div className="box2" onClick="productWarpHome()">
                            <p className="description">Home Audio</p>
                        </div>

                    </div>

                </section>




                {/* ABOUT US PAGE LINK OFF */}

                <section id="aboutUsBox">

                    <div className="aboutHomeContainer">

                        <p className="homeAboutUs">Our team of talented audiophiles has been dedicated to providing the best quality products and services for over a decade.

                            {/* <a className="warpToProducts" href="" style="text-decoration: none;">Learn More!</a> */}
                        </p>

                        {/* <!-- <a className="warpToProducts" href="" style="text-decoration: none;">Learn More!</a> --> */}

                    </div>

                </section>


                {/* CONTACT US PAGE LINK OFF */}

                <section id="contactUsHome">

                    <div className="homeContactContainer">

                        <div className="colorOverlay"></div>
                        <div className="colorOverlay2"></div>

                        <p className="homeContactText">

                            Are you looking for technical support or service?
                            Do you have any questions about a product warranty or specs?

                            Leave us a message and someone from our talented team of professionals with reach out shortly to help in any way we can!

                        </p>

                        {/* <!-- <p className="contactLearnMore"> -->
                        <a className="contactLearnMore" href="" style="text-decoration: none;">Learn More!</a>
                         <!-- </p> --> */}

                    </div>

                </section>



            </main>

            <Footer />

        </div >
    )
}

export default Homepage
